				foreach ((array) $this->collMeals as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMenus) {
				foreach ((array) $this->collMenus as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collVotes) {
				foreach ((array) $this->collVotes as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collMeals = null;
		$this->collMenus = null;
		$this->collVotes = null;
	}

	// symfony_behaviors behavior
	
	/**
	 * Calls methods defined via {@link sfMixer}.
	 */
	public function __call($method, $arguments)
	{
	  if (!$callable = sfMixer::getCallable('BasePlace:'.$method))
	  {
	    throw new sfException(sprintf('Call to undefined method BasePlace::%s', $method));
	  }
	
	  array_unshift($arguments, $this);
	
	  return call_user_func_array($callable, $arguments);
	}
	
